Compose_Email_Title.inputs:
@if(
   greater(outputs('Compose_Statement_Version_Number'), 1), 
   if(
      equals(parameters('EnvironmentName (bs_environmentName)'), 'PROD'),
      concat(item()?['siteNumber'], ' Towne Park Invoice (Revised)'),
      concat('[', parameters('EnvironmentName (bs_environmentName)'), '] - ', item()?['siteNumber'], ' Towne Park Invoice (Revised)')
   ),
   if(
      equals(parameters('EnvironmentName (bs_environmentName)'), 'PROD'),
      concat(item()?['siteNumber'], ' Towne Park Invoice'),
      concat('[', parameters('EnvironmentName (bs_environmentName)'), '] - ', item()?['siteNumber'], ' Towne Park Invoice')
   )
)
