parameters.$apply:
filter(
      PERIOD eq @{formatDateTime(triggerBody()?['date'], 'yyyyMM')}
      AND COST_CENTER eq '@{triggerBody()?['text_1']}' 
      AND  @{outputs('Compose_main_account_data_query_string')}
)
/aggregate(
    BALANCE with sum as BillablePayroll
)

filter(
    PERIOD eq @{formatDateTime(triggerBody()?['date'], 'yyyyMM')} 
    AND COST_CENTER eq '@{triggerBody()?['text_1']}'
    AND (MAIN_ACCOUNT eq '6200' OR MAIN_ACCOUNT eq '6399' OR MAIN_ACCOUNT eq '6500')
)
/aggregate(
    BALANCE with sum as PTEB
)
