Compose_Tier_Amount.inputs:
@if(
    or(
        equals(outputs('Compose_Current_Limit'), 0),
        lessOrEquals(
            outputs('Compose_-_Total_Profit'),
            outputs('Compose_Current_Limit')
        )
    ),
    sub(
        outputs('Compose_-_Total_Profit'),
        variables('PreviousLimit')
    ),
    sub(
        outputs('Compose_Current_Limit'),
        variables('PreviousLimit')
    )
)

Compose_-Profit_Accumulation_Start_Date.inputs:
@if(
    equals(first(body('Parse_JSON_-_Management_Agreement_Object')).bs_profitshareaccumulationtype, 
    126840001), 
    concat(
        formatDateTime(utcNow(), 'yyyy'), 
        '-01-01'
    ), 
    if(
        greaterOrEquals(
            formatDateTime(utcNow(), 'MMdd'), 
            formatDateTime(triggerBody()?['date_2'], 'MM-dd')
        ),
        concat(
            formatDateTime(utcNow(), 'yyyy'), '-', 
            formatDateTime(triggerBody()?['date_2'], 'MM-dd')
        ),
        concat(
            sub(
                int(formatDateTime(utcNow(), 'yyyy')), 
                1
            ), '-', 
            formatDateTime(triggerBody()?['date_2'], 'MM-dd')
        )
    )
)
